import React from 'react';

function App() {

  usesState = {
    name : ''
  }

  const handleChange = (e) => {
    useState({
      name : e.target.value
    })
  }

  return (
    <div className="App">
      Form
      <form>
        <input type='text' onChange={handleChange}/>
        <button>Submit</button>
      </form>
      {useState.name}
    </div>
  );
}

export default App;
